	/**
	 * The function is building the orders record HTML
	 * @param {object} response - JSON with all of the orders.
	 */
	function ShowOrders( response ) {

		// Generate the orders HTML 
		$('.orders .orders-container').append(GetOrdersHTML(response.orders));

		// Get object
		var $ordersTable = $('.client-zone-orders-table');

		// Create click event for the `Info` link
		$ordersTable.on('click','.view-more', function() {

			// Get objects
			var $showMoreInfo = $(this);
			var uniqueID = $showMoreInfo.data('id');
			var $order = $ordersTable.find('tr[data-id="'+uniqueID+'"]');
			var $orderInfo = $order.find('.order-info');
			var $loadingIcon = $order.find('.loading-icon');

			// If the order info is shown then hide it
			if ( $orderInfo.length > 0 ) {
				$showMoreInfo.html("Info");
				$order.fadeOut(fadeInterval, function() {
					$loadingIcon.show();
					$orderInfo.remove();
				});
			// Send AJAX request to get the order info
			} else {
				ShowClientOrderInfo($order);
			}
		});

		// Show the profile table after the client login
		$('.profile').show();
		$('.clientZoneTabs').show();
		$('.clientZoneProfileImage').show();
		$('.client-zone-log-out').show();
	}

