	/**
	 * The function is generating html of the orders records.
	 * @param  {object} orders - orders array.
	 * @return {string} html - orders table.
	 */
	function GetOrdersHTML( orders ) {

		var html = '';			
		
		html += '<table class="client-zone-orders-table table table-bordered">';
			html += '<thead>';
				html += '<tr>';
					html += '<th>#</th>';
					html += '<th>'+"Name"+'</th>';
					html += '<th>'+"Time"+'</th>';
					html += '<th>'+"Total Price"+'</th>';
					html += '<th>'+"Status"+'</th>';
					html += '<th>'+"Action"+'</th>';
				html += '</tr>';
			html += '</thead>';
			html += '<tbody>';
			if ( orders.noOrders ) {
				html += '<tr>';
					html += '<td colspan="6" class="text-center">'+"You do not have any orders yet."+'</td>';
				html += '</tr>';
			} else {
				$.each(orders, function( index, order) {
					// if order is empty the no need to show it
					if (order.length == 0 ) return;
					// add order record
					$.each(order, function( index, singleOrder ) {
						html += GenerateOrderRecordHTML(singleOrder);
					});
				});
			}
			html += '</tbody>';
		html += '</table>';
		
			
		// Return the HTML 
		return html;
	}

	/**
