			html += '<td>';
				html += '<strong>'+order.ModuleTitle+'</strong>';
				html += '<br>';
				html += order.uniqueCodeID;
				html += '<div class="c-z-o-t-mobile-responsive">';
					html += '<div>';
						if ( moduleHasCustomFields.indexOf(order.moduleTypeNUM) != -1 ) {
							html += order.first_name +' '+ order.last_name;
						} else {
							html += order.name;	
						}
					html += '</div>';
					html += '<div>';
						html += order.time;
					html += '</div>';
					html += '<div>';
						html += order.totalPrice;
					html += '</div>';
					html += '<div style="margin-top:15px;">';
						html += '<a href="javascript:void(0);" data-id="'+order.uniqueCodeID+'" class="view-more btn btn-sm btn-primary btn-xs">'+"Info"+'</a>';
					html += '</div>';
				html += '</div>';
			html += '</td>';
			html += '<td>';
				html += '<div>';
					if ( moduleHasCustomFields.indexOf(order.moduleTypeNUM) != -1 ) {
						html += order.first_name +' '+ order.last_name;
					} else {
						html += order.name;	
					}
				html += '</div>';
			html += '</td>';
			html += '<td>';
				html += order.time;
			html += '</td>';
			html += '<td>';
				html += order.totalPrice;
			html += '</td>';
			html += '<td>';
				if ( order.status == 2 ) {
					html += '<span>'+"Paid"+'</span>';
				} else {
					html += '<span>'+"Unpaid"+'</span>';
				}
			html += '</td>';
			html += '<td class="action-col">';
				html += '<a href="javascript:void(0);" data-id="'+order.uniqueCodeID+'" class="view-more btn btn-sm btn-primary ">'+"Info"+'</a>';
				if ( order.status != 2 ) {
					html += '<a href="/?orderScreen=2&w=&websiteID=2630165&orderID='+order.uniqueCodeID+'&cartType='+order.moduleTypeNUM+'" target="_blank" class="btn btn-sm btn-primary">'+"Pay"+'</a>';
				}
			html += '</td>';
		html += '</tr>';

		return html;
	}

	/**
	 * The function handle the browser history.
	 */
	function BrowserHistoryHandler() {
		// exit if the browser doesn't support history handler
		if ( !window.history || !window.history.pushState ) return;
		// handle the browser history
		$(window).on('popstate', function() {
			// check what step we need to show
			switch (window.location.hash) {
				case '#passwordStep':
					// tabs
					$('.orders').hide();
					$('.clientZoneTabs').hide();
					// logout
					$('.client-zone-log-out').hide();
					// login
					$loginFormsContainer.show();
					$clientEmailForm.hide();
					$clientPasswordForm.show();
					break;
				case '#orders-2':
				case '#orders-9':
				case '#orders-10':
				case '#orders-15':
				case '#orders-37':
				case '#orders-96':
				case '#orders-97':
				case '#orders-112':
					// select the orders tab
					SelectTab(window.location.hash.substring(1, window.location.hash.length));
					// logout
					$('.client-zone-log-out').show();
					// login
					$clientEmailForm.hide();
					$clientPasswordForm.hide();
					$loginFormsContainer.hide();
					break;
				case '#profile':
					// select the orders tab
					SelectTab('profile');
					// logout
					$('.client-zone-log-out').show();
					// login
					$clientEmailForm.hide();
					// $clientPasswordForm.hide();
					$loginFormsContainer.hide();
					break;
				default: 
					// if the user logged in we show the orders tab, otherwise the login form
					if ( czIsLoggedIn ) {
						// select the orders tab
						SelectTab('profile');
						// logout
						$('.client-zone-log-out').show();
						// login
						$clientEmailForm.hide();
						// $clientPasswordForm.hide();
						$loginFormsContainer.hide();
					} else {
						// tabs
						$('.orders').hide();
						$('.clientZoneTabs').hide();
						// logout
						$('.client-zone-log-out').hide();
						// login
						$loginFormsContainer.show();
						// $clientPasswordForm.hide();
						$clientEmailForm.show();
						// resend password
						$resendPasswordEmailSent.hide();
						$resendPasswordLoading.hide();
						$resendPasswordBtn.show();
					}
			}
		});
	}
	
	/***
	 * The function is saving the step number to history
	 */
	function SaveStepToHistory(step) {
		if ( !window.history || !window.history.pushState ) return;
		window.history.pushState('forward', null, '#'+step);
	}

